<script>
import FlipCountdown from "vue2-flip-countdown";
const scriptURL =
  "https://script.google.com/macros/s/AKfycbxukw_bpvnnZ3phBKsCXaDA_S6jM6E5UvowCSuP9ApcwTf5sNI/exec";
// const form = document.forms['submit-to-google-sheet']
export default {
  layout: "landing",
  components: { FlipCountdown },
  data() {
    return {
      ticker: "WIFI",
      name: "WinFinance",
      email: "",
      telegram: "",
      wallet: "",
      twitter: "",
      submitted: "",
      failed: "",
      notif: "",
      terms: false,
    };
  },
  methods: {
    async sendForm() {
      this.submitted = "";
      this.notif = "Sending...";
      this.failed = "";
      let data = new FormData();
      data.append("email", this.email);
      data.append("twitter", this.twitter);
      data.append("telegram", this.telegram);
      data.append("wallet", this.wallet);

      fetch(scriptURL, { method: "POST", body: data })
        .then((response) => (this.submitted = "Submitted Successfully"))
        .then((response) => (this.notif = ""))
        .catch((error) => (this.failed = error.message));
    },
  },
  computed: {
    isDisabled: function () {
      return !this.terms;
    },
  },
};
</script>